package com.philomath.littlelemonimport androidx.compose.foundation.Imageimport androidx.compose.foundation.borderimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.foundation.shape.CircleShapeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.clipimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.unit.dpimport androidx.navigation.NavHostController@Composablefun HomeScreen(navController: NavHostController) {      Column {            Row(                  horizontalArrangement = Arrangement.SpaceBetween,                  verticalAlignment = Alignment.CenterVertically,                  modifier = Modifier                        .padding(12.dp)                        .fillMaxWidth()            ) {                  Image(                        painter = painterResource(id = R.drawable.logo),                        contentDescription = "Little Lemon"                  )                  Image(                        modifier = Modifier                              .clickable {                                    navController.navigate(Profile.route)                              }                              .size(40.dp)                              .clip(CircleShape)                              .border(1.dp, Color.Transparent, CircleShape),                        painter = painterResource(id = R.drawable.profile),                        contentDescription = "Little Lemon"                  )            }      }}