package com.philomath.littlelemonimport androidx.compose.foundation.BorderStrokeimport androidx.compose.foundation.Imageimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.Buttonimport androidx.compose.material3.ButtonDefaultsimport androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.Textimport androidx.compose.material3.TextFieldimport androidx.compose.runtime.Composableimport androidx.compose.runtime.getValueimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.rememberimport androidx.compose.runtime.setValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.RectangleShapeimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.text.input.TextFieldValueimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dp@OptIn(ExperimentalMaterial3Api::class)@Composablefun Onboarding() {      var firstName by remember { mutableStateOf(TextFieldValue("")) }      var secondName by remember { mutableStateOf(TextFieldValue("")) }      var email by remember { mutableStateOf(TextFieldValue("")) }      Column(            verticalArrangement = Arrangement.Center,            ) {            Image(                  painter = painterResource(R.drawable.logo),                  contentDescription = "Little Lemon",                  modifier = Modifier.align(alignment = Alignment.CenterHorizontally)            )            Text(                  text = "Let's get to know you",                  modifier = Modifier                        .align(alignment = Alignment.CenterHorizontally)                        .padding(12.dp)            )            TextField(                  value = firstName,                  onValueChange = { firstName = it },                  label = { Text(text = "First Name") },                  placeholder = { Text(text = "Enter First Name") },                  modifier = Modifier.padding(12.dp)            )            TextField(                  value = secondName,                  onValueChange = { secondName = it },                  label = { Text(text = "Second Name") },                  placeholder = { Text(text = "Enter Second name") },                  modifier = Modifier.padding(12.dp)            )            TextField(                  value = email,                  onValueChange = { email = it },                  placeholder = { Text(text = "Enter email") },                  label = { Text(text = "Email") },                  modifier = Modifier.padding(12.dp)            )            Button(                  onClick = { },                  colors = ButtonDefaults.buttonColors(                        containerColor = Color.Yellow,                        contentColor = Color.Black                  ),                  shape = RoundedCornerShape(15.dp),                  border = BorderStroke(1.dp, Color.Black),                  modifier = Modifier.align(alignment = Alignment.CenterHorizontally)            ) {                  Text(text = "Register")            }      }}@Preview(showBackground = true)@Composablefun OnboardingPreview() {      Onboarding()}